import { Purchase, User } from '~/models'

import { getFormattedNumber } from './get-formatted-number'

const getCostPerPerson = (purchase: Purchase) => {
	if (!purchase.cost) {
		return 0
	}

	if (!purchase.users.length) {
		return 0
	}

	return purchase.cost / purchase.users.length
}

const getTotal = (purchases: Purchase[]) => {
	return purchases.reduce((acc, curr) => {
		return acc + (curr.cost || 0)
	}, 0)
}

const getUsersPurchases = (purchases: Purchase[]) => {
	const purchasesByUser = new Map<User, Purchase[]>([])

	purchases.forEach((purchase) => {
		purchase.users.forEach((user) => {
			purchasesByUser.set(user, [...purchasesByUser.get(user) || [], purchase])
		})
	})

	return [...purchasesByUser.entries()].map(([user, purchases]) => {
		const totalPerUser = purchases.reduce((acc, curr) => acc + getCostPerPerson(curr), 0)

		const title = `${user.name} (${getFormattedNumber(totalPerUser)})`

		const details = purchases.map((p) => {
			return `${p.title} — ${getFormattedNumber(getCostPerPerson(p))}`
		})

		return { title, details }
	})
}

