const addLineBreak = (value: string, count = 1) => {
	return value + '\n'.repeat(count)
}

export const getCopyString = (purchases: Purchase[], ignoreDetails: boolean): string => {
	const start = addLineBreak(`Fart: ${new Date().toLocaleDateString()} at ${new Date().toLocaleTimeString()}`, 2)
	const end = `Total: ${getFormattedNumber(getTotal(purchases))}`

	const content = getUsersPurchases(purchases).reduce((acc, curr) => {
		let result = acc + addLineBreak(`${getRandomColorEmoji()} ${curr.title}`)

		if (ignoreDetails) {
			return addLineBreak(result)
		}

		result += curr.details.map((d) => addLineBreak(`+ ${d}`)).join('')

		return addLineBreak(result)
	}, '')

	return `${start}${content}${end}`
}